mapper.insert:
INSERT INTO orders (
                     user_id
                   , status
                   , total_price
                   ) VALUES
                   (
                     #{userId}
                   , #{status}
                   , #{totalPrice}
                   )

mapper.update:
UPDATE orders
   SET status     = #{status}
     , updated_at = CURRENT_TIMESTAMP
 WHERE id = #{id}
