mapper.insert:
INSERT INTO products (
                       name
                     , description
                     , price
                     , stock
                     , thumbnail_url
                     , created_by
                     ) VALUES
                     (
                       #{name}
                     , #{description}
                     , #{price}
                     , #{stock}
                     , #{thumbnailUrl}
                     , #{createdBy}
                     )

mapper.select:
SELECT price
  FROM product
 WHERE id = #{productId}

mapper.update:
UPDATE products
   SET name = #{name}
     , description = #{description}
     , price = #{price}
     , stock = #{stock}
     , thumbnail_url = #{thumbnailUrl}
     , updated_at = CURRENT_TIMESTAMP
 WHERE id = #{id}
